@namespace
class SpriteKind:
    left_paddle = SpriteKind.create()
    rightpaddle = SpriteKind.create()

def on_on_overlap(sprite, otherSprite):
    sprite.vx = sprite.vx * -1
    info.change_score_by(1)
    music.ba_ding.play()
sprites.on_overlap(SpriteKind.player, SpriteKind.left_paddle, on_on_overlap)

